mapper.select:
  
    select X.rnum, X.bno, X.uname, X.title, X.reviewcon, X.regdate, X.viewctn, X.updatedate
from ( 
    select rownum as rnum, A.bno, A.uname, A.title, A.reviewcon, A.regdate, A.viewctn, A.updatedate
    from (
        select bno, uname, title, reviewcon, regdate, viewctn ,updatedate
        from tbl_board_review

        order by bno desc) A

    where rownum <= #{perPageNum} * #{page}                   
    
            
              ) X
where X.rnum >= ((#{page} - 1) * 10)+1
