mapper.select:
	
	
	select rtid, rtname, rtaddress, regdate, updatedate 
	from 
	  (
	  select rownum rno, rtid, rtname, rtaddress, regdate, updatedate
	  from tbl_rooftop 
	  where rtid > 0 
	  and rownum <= #{page} * #{perPageNum} 
	  order by rtid desc
	  ) 
	where rno > (#{page} - 1) * #{perPageNum}

	
	select rtid, rtname, rtaddress, regdate, updatedate 
	from 
	  (
	  select rownum rno, rtid, rtname, rtaddress, regdate, updatedate
	  from tbl_rooftop 
	  where rtid > 0 
	  
	  
	  
	  
	  
	  and rownum <= #{page} * #{perPageNum} 
	  order by rtid desc
	  ) 
	where rno > (#{page} - 1) * #{perPageNum}
